import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001/api';

export const generateMaterial = async (prompt: string, model: 'gpt3' | 'gpt4' | 'ollama' = 'ollama'): Promise<string> => {
  try {
    const { data } = await axios.post<{ material: string }>(`${API_URL}/generate`, { prompt, model: model === 'ollama' ? 'llama2' : model });
    return data.material;
  } catch (error) {
    console.error('Error generating material:', error);
    throw error;
  }
};

export const evaluateMaterial = async (material: string, model: 'gpt4' | 'ollama' = 'ollama'): Promise<number> => {
  try {
    const { data } = await axios.post<{ score: number }>(`${API_URL}/evaluate`, { material, model: model === 'ollama' ? 'llama2' : model });
    return data.score;
  } catch (error) {
    console.error('Error evaluating material:', error);
    throw error;
  }
};
